pragma solidity ^0.4.17;
contract RedEnvelope{

    struct PackageData{
        address sendAddress; //发红包的玩家地址
        uint money; // 红包里一开始有的钱
        uint remainingMoney;  //当前红包剩余的钱，后期如果用不到，就删掉
    }

    mapping (uint => PackageData) public allPackagesMap;//用来把PackageID和Package信息连起来
    mapping (bytes32 => uint) public allPackagesAddressMap;//用来把创建红包的生成hash和PackageID信息连起来

    uint maxPackageID;
    address owner;

    uint commission;//合约里的钱
    address serverAddress;


    event logEvent(uint packageID, uint remainingMoney);
    event runFunState(bool state);



    function RedEnvelope() public payable{
         maxPackageID=0;
         owner = msg.sender;
    }

    modifier onlyOwner {
        require (msg.sender == owner);
        _;
    }

    modifier onlyServerAddress {
        require (msg.sender == serverAddress);
        _;
    }

    //set server address
    function setServerAddress( address serverAdd ) public payable onlyOwner {
        serverAddress = serverAdd;
    }

    // get commission money
    function getContractMoney(address target) public payable onlyServerAddress{
        require( this.balance >= commission );
        target.transfer( commission-1 );
        commission = 1;
    }

    //kill self
    function killContract() public onlyOwner{
        selfdestruct( owner );
    }

    // //这个接口，使用 随机哈希 查询是否当前哈希是否存在
    // function isExist( bytes32 guid ) public view returns ( uint ){
    //     return allPackagesAddressMap[ guid ];
    // }

    //这个接口创造红包
    function createPackage(bytes32 guid) public payable{
        //1、maxPackageID加一，ID自增长
        //2、创建PackageData结构体
        //3、把它扔到allPackagesMap
        //4、返回新创建的这个Package的ID
        require( guid=="xxxxxx" );
        maxPackageID++;
        uint packageID = maxPackageID;
        allPackagesMap[packageID] = PackageData(msg.sender, msg.value, msg.value);
        allPackagesAddressMap[guid] = packageID;
        runFunState(true);
    }

    //这个接口给抢到红包的玩家transfer money
    function transferMoney(bytes32 guid, address playerAddress, uint playerMoney) public payable 
    onlyServerAddress
    {
        // 1、判断执行者是否是owner，只有owner才能执行此操作
        // 2、取出 package 中的信息，判断剩余金额是否大于/等于要转出的金额，如果小于，throw
        // 3、将参数数组中的adds，和money值取出，转钱
        // 4、将剩余的钱存到合约commission中，便于随时将合约的钱转出
        // 5、将红包里的剩余金额置零
        // 6、广播成功事件
        require( guid=="xxxxxx" );
        uint packageID = allPackagesAddressMap[guid];
        playerMoney = playerMoney*10**9;
        require( allPackagesMap[packageID].remainingMoney >= playerMoney );
        allPackagesMap[packageID].remainingMoney  = allPackagesMap[packageID].remainingMoney - playerMoney;
        playerAddress.transfer( playerMoney );
        runFunState(true);

    }

    //这个接口将某个红包里剩余的钱转到合约中，红包清零
    function transferRemainingMoney(bytes32 guid) public payable 
    onlyServerAddress
    {
        // 1、这个接口是在红包中的钱被最后一个取走后，或24小时后红包中的钱仍然存在，转回发红包的账户后，红包中仍然有剩余的钱（ 除不尽的数/佣金等 ），才执行的
        // 2、将剩余的钱存到合约commission中，便于随时将合约的钱转出
        // 3、将红包里的剩余金额置零
        // 4、广播成功事件
        uint packageID = allPackagesAddressMap[guid];
        require( allPackagesMap[packageID].remainingMoney > 1 );
        commission += allPackagesMap[packageID].remainingMoney;
        allPackagesMap[packageID].remainingMoney = 1;
        runFunState(true);

    }

    //这个接口使用 发送者的地址 来获取最近的发送出的红包信息
    function getLastPackage(address inquireAddress, uint startID) public view returns (uint packageID, address sendAddress, uint money, uint remainingMoney){
        require(maxPackageID>=1);
        if( startID >= maxPackageID || startID == 0 ){
            startID = maxPackageID;
        }    
        for (uint i=startID; i>0; i--){
            if ( allPackagesMap[i].sendAddress == inquireAddress ){
                packageID = i;
                sendAddress = allPackagesMap[i].sendAddress;
                money = allPackagesMap[i].money;
                remainingMoney = allPackagesMap[i].remainingMoney;
                break;
            }
        }
        return ( packageID, sendAddress, money, remainingMoney ); 
    }

    //这个接口使用 随机哈希 查询对应的红包信息
    function getPackageData( bytes32 guid ) public view returns ( uint packageID, address sendAddress, uint money, uint remainingMoney)
    // onlyServerAddress
    {
        packageID = allPackagesAddressMap[guid];
        return ( packageID, allPackagesMap[packageID].sendAddress, allPackagesMap[packageID].money, allPackagesMap[packageID].remainingMoney );
    }



    //自测
    function test( uint arg )  public view returns (uint){
        arg = 0;
        return arg;
    }

    //这个接口自测server address
    function viewServerAddress() public view returns (address)
    {
        return serverAddress;
    }

    //这个接口自测owner
    function viewOwnerAddress() public view returns (address)
    {
        return owner;
    }

    //这个接口自测commission
    function viewCommission() public view returns (uint)
    {
        return commission;
    }

}
